(1619, 172, '_form', '<div id="contact-form">\n  <form action="bmz/raschet-stoimosti-zdaniya(bmz)" method="post" enctype="multipart/form-data">\n    <br>\n    <table width="100%" height="50" cellspacing="1" cellpadding="1" border="0">\n      <tbody>\n        <tr>\n          <td valign="top" width="50%"><b>1. <u>Назначение здания: </u></b></td>\n          <td width="" style=" vertical-align: top; " height="1">\n\n\n[select purpose "Не указано" "Складское" "Сельскохозяйственное" "Производственное" "Спортивное" "Другое"]\n\n\n          </td>\n        </tr>\n      </tbody>\n    </table>\n    <table width="100%" height="50" cellspacing="1" cellpadding="1" border="0">\n      <tbody>\n        <tr>\n          <td valign="top" width="50%"><b>2. <u>Район строительства (Область, Город)</u>&nbsp;</b>\n            <br>\n            <br>\n          </td>\n          <td width="" height="1" valign="top" align="left">\n\n\n[text region class:inputtext]\n\n\n          </td>\n        </tr>\n      </tbody>\n    </table>\n    <table width="100%" height="50" cellspacing="1" cellpadding="1" border="0">\n      <tbody>\n        <tr>\n          <td valign="top" width="145"><b>3. <u>Размеры здания: </u></b></td>\n          <td width="" height="1" valign="top" align="left">\n            <span class="grey_dark">[text plan_length class:inputtext class:inputtext__small]<text align="top"><i>  Длина<b>(м)</b>;</i> &nbsp;</text>[text planwidth class:inputtext class:inputtext__small]<text align="top"><i>  Ширина<b>(м)</b>;</i> &nbsp;</text>[text plane_height class:inputtext class:inputtext__small]<text align="top"><i>  Высота<b>(м)</b>;</i> &nbsp;</text></span>\n          </td>\n        </tr>\n      </tbody>\n    </table>\n    <table width="100%" height="50" cellspacing="1" cellpadding="1" border="0">\n      <tbody>\n        <tr>\n          <td valign="top" width="50%"><b>4. <u>Этажность здания:</u></b></td>\n          <td width="" height="1" valign="top" align="left">\n\n[text stores  class:inputtext__small]\n\n\n\n          </td>\n        </tr>\n      </tbody>\n    </table>\n    <table width="100%" height="50" cellspacing="1" cellpadding="1" border="0">\n      <tbody>\n        <tr>\n          <td valign="top" width="50%"><b>5. <u>Фундаменты:</u></b></td>\n          <td width="" height="1" valign="top" align="left">\n\n\n\n[select fundament "Не указано" "Существующие" "Новое строительство"]\n\n\n          </td>\n        </tr>\n      </tbody>\n    </table>\n    <table width="100%" height="50" cellspacing="1" cellpadding="1" border="0">\n      <tbody>\n        <tr>\n          <td valign="top" width="50%"><b>6. <u>Тип крыши:</u></b></td>\n          <td width="" height="1" valign="top" align="left">\n\n\n[select roof "Не указано" "Односкатная" "Двускатная" "Другое"]\n\n\n          </td>\n        </tr>\n      </tbody>\n    </table>\n    <table width="100%" height="50" cellspacing="1" cellpadding="1" border="0">\n      <tbody>\n        <tr>\n          <td valign="top" width="50%"><b>7. <u>Наличие мансардного этажа:</u></b></td>\n          <td width="" height="1" valign="top" align="left">\n\n\n[select mansard "Не указано" "Да" "Нет" "Другое"]\n\n\n          </td>\n        </tr>\n      </tbody>\n    </table>\n    <table width="100%" height="50" cellspacing="1" cellpadding="1" border="0">\n      <tbody>\n        <tr>\n          <td valign="top" width="50%"><b>8. <u>Тип кровельного покрытия:</u></b></td>\n          <td width="" height="1" valign="top" align="left">\n\n\n[select roof_type "Не указано" "Металлочерепица" "Профнастил" "Фальцевая кровля" "Кровельные ромбы" "Другое"]\n\n\n          </td>\n        </tr>\n      </tbody>\n    </table>\n    <table width="100%" height="50" cellspacing="1" cellpadding="1" border="0">\n      <tbody>\n        <tr>\n          <td valign="top" width="50%"><b>9. <u>Тип фасадной отделки: </u></b></td>\n          <td width="" height="1" valign="top" align="left">\n\n\n[select wall "Не указано" "Профнастил" "Металлический сайдинг (горизонтальный, вертикальный)" "Фасадные ромбы" "Касеты (открытого, закрытого монтажа)" "Другое"]\n\n\n          </td>\n        </tr>\n      </tbody>\n    </table>\n    <table width="100%" height="50" cellspacing="1" cellpadding="1" border="0">\n      <tbody>\n        <tr>\n          <td valign="top" width="20%"><b>10. <u>Окна:</u></b></td>\n          <td width="" height="1" valign="top" align="left">[text kl_windows class:inputtext class:inputtext__small]&nbsp;&nbsp;<i text="" align="top" style="font-size: 13px; line-height: 20px;">Кол-во,&nbsp;</i><span style="font-style: normal;">&nbsp; &nbsp; </span>[text kl_windows_1 class:inputtext class:inputtext__small]<span style="font-style: normal;">&nbsp;</span><span style="font-style: normal;">&nbsp;X &nbsp;</span>[text kl_windows_2 class:inputtext class:inputtext__small]&nbsp;&nbsp;Размеры, <b>(мм).</b><i>&nbsp;&nbsp;</i>\n          </td>\n        </tr>\n      </tbody>\n    </table>\n    <table width="100%" height="50" cellspacing="1" cellpadding="1" border="0">\n      <tbody>\n        <tr>\n          <td valign="top" width="20%"><b>11. <u>Двери:</u></b></td>\n          <td width="" height="1" valign="top" align="left">[text kl_doors class:inputtext class:inputtext__small]&nbsp;&nbsp;<i text="" align="top" style="font-size: 13px; line-height: 20px;">Кол-во,&nbsp;</i><span style="font-style: normal;">&nbsp; &nbsp; </span>[text kl_doors_1 class:inputtext class:inputtext__small]<span style="font-style: normal;">&nbsp;</span><span style="font-style: normal;">&nbsp;X &nbsp;</span>[text kl_doors_2 class:inputtext class:inputtext__small]&nbsp;&nbsp;Размеры, <b>(мм).</b><i>&nbsp;&nbsp;</i>\n          </td>\n        </tr>\n      </tbody>\n    </table>\n    <table width="100%" height="50" cellspacing="1" cellpadding="1" border="0">\n  <tbody>\n    <tr>\n      <td valign="top" width="50%"><b>12. <u>Выполнение строительно-монтажных работ:</u></b></td>\n      <td width="" height="1" valign="top" align="left">\n\n\n[select project "Не указ